mapper.select:
select natatoriumId,name,waterLevel,swimmingPoolId,numberOfPools,waterTempertaure,detail
from natatorium n

    1=1

        and n.natatoriumId = #{natatoriumId}


        and n.name = #{name}


select natatoriumId,name,waterLevel,swimmingPoolId,numberOfPools,waterTempertaure,detail
from natatorium n

    1=1

        and n.swimmingPoolId = #{swimmingPoolIdId}


        and n.name = #{name}



order by swimmingPoolId limit #{pageNum},#{limit}

select count(*) from natatorium n

    1=1

        and n.natatoriumId = #{natatoriumId}


        and n.name = #{name}

mapper.insert:
insert into natatorium(name,waterLevel,swimmingPoolId,numberOfPools,waterTempertaure,detail)
select #{name},#{waterLevel},#{swimmingPoolId},#{numberOfPools},#{waterTempertaure},#{detail}
where not exists(select 1 from natatorium where  natatoriumId=#{natatoriumId})
